scripts/optimize-images.js.content:
// Image optimization script for better SEO and performance
const fs = require('fs').promises;
const path = require('path');
const { execSync } = require('child_process');

// Configuration
const PUBLIC_DIR = path.join(__dirname, '../public');
const IMAGE_EXTENSIONS = ['.jpg', '.jpeg', '.png', '.webp'];
const QUALITY = 85;

async function optimizeImages() {
  console.log('Starting image optimization...');

  try {
    // Check if sharp is installed
    try {
      require.resolve('sharp');
      console.log('Sharp is installed. Proceeding with optimization...');
    } catch (e) {
      console.log('Installing sharp package for image optimization...');
      execSync('npm install sharp --no-save');
    }

    // Recursively find all image files
    const imageFiles = await findImageFiles(PUBLIC_DIR);
    console.log(`Found ${imageFiles.length} images to optimize.`);

    if (imageFiles.length === 0) {
      console.log('No images found to optimize.');
      return;
    }

    // Import sharp after ensuring it's installed
    const sharp = require('sharp');

    // Process each image
    for (const file of imageFiles) {
      const ext = path.extname(file).toLowerCase();
      const webpOutput = file.replace(ext, '.webp');

      // Skip already optimized files
      if (ext === '.webp' && await fileExists(file.replace('.webp', '.webp.original'))) {
        console.log(`Skipping already optimized: ${path.basename(file)}`);
        continue;
      }

      // Backup original if not already backed up
      const originalBackup = `${file}.original`;
      if (!(await fileExists(originalBackup))) {
        await fs.copyFile(file, originalBackup);
      }

      // Optimize the original format
      await sharp(file)
        .resize(2048, null, { withoutEnlargement: true })
        .jpeg({ quality: QUALITY, progressive: true })
        .png({ compressionLevel: 9, progressive: true })
        .toFile(`${file}.temp`);

      await fs.rename(`${file}.temp`, file);

      // Create WebP version (better compression, modern browsers)
      await sharp(originalBackup)
        .resize(2048, null, { withoutEnlargement: true })
        .webp({ quality: QUALITY })
        .toFile(webpOutput);

      console.log(`Optimized: ${path.basename(file)} (and created WebP version)`);
    }

    console.log('Image optimization complete!');
  } catch (error) {
    console.error('Error during image optimization:', error);
  }
}

async function findImageFiles(dir, allFiles = []) {
  const files = await fs.readdir(dir);

  for (const file of files) {
    const filePath = path.join(dir, file);
    const stat = await fs.stat(filePath);

    if (stat.isDirectory()) {
      await findImageFiles(filePath, allFiles);
    } else {
      const ext = path.extname(file).toLowerCase();
      if (IMAGE_EXTENSIONS.includes(ext)) {
        allFiles.push(filePath);
      }
    }
  }

  return allFiles;
}

async function fileExists(filePath) {
  try {
    await fs.access(filePath);
    return true;
  } catch {
    return false;
  }
}

// Run the optimizer
optimizeImages().catch(console.error);

server/paypal.ts.content:
import {
  Client,
  Environment,
  LogLevel,
  OAuthAuthorizationController,
  OrdersController,
} from "@paypal/paypal-server-sdk";
import { Request, Response } from "express";

/* PayPal Controllers Setup */

const { PAYPAL_CLIENT_ID, PAYPAL_CLIENT_SECRET } = process.env;

if (!PAYPAL_CLIENT_ID) {
  throw new Error("Missing PAYPAL_CLIENT_ID");
}
if (!PAYPAL_CLIENT_SECRET) {
  throw new Error("Missing PAYPAL_CLIENT_SECRET");
}

const client = new Client({
  clientCredentialsAuthCredentials: {
    oAuthClientId: PAYPAL_CLIENT_ID,
    oAuthClientSecret: PAYPAL_CLIENT_SECRET,
  },
  timeout: 0,
  environment:
    process.env.NODE_ENV === "production"
      ? Environment.Production
      : Environment.Sandbox,
  logging: {
    logLevel: LogLevel.Info,
    logRequest: {
      logBody: true,
    },
    logResponse: {
      logHeaders: true,
    },
  },
});

const ordersController = new OrdersController(client);
const oAuthAuthorizationController = new OAuthAuthorizationController(client);

/* Token generation helpers */

export async function getClientToken() {
  const auth = Buffer.from(
    `${PAYPAL_CLIENT_ID}:${PAYPAL_CLIENT_SECRET}`,
  ).toString("base64");

  const { result } = await oAuthAuthorizationController.requestToken(
    {
      authorization: `Basic ${auth}`,
    },
    { intent: "sdk_init", response_type: "client_token" },
  );

  return result.accessToken;
}

/* Subscription Plans */
export const SUBSCRIPTION_PLANS = {
  USD: {
    planId: "P-5ML4271244454362WXNWU5NQ", // PayPal plan ID for $1 USD
    price: "1.00",
    currency: "USD"
  },
  INR: {
    planId: "P-1GF589577P007863KXNWU5NQ", // PayPal plan ID for â‚¹100 INR
    price: "100.00", 
    currency: "INR"
  }
};

/* Subscription management using Orders API for recurring payments */

export async function createSubscription(req: Request, res: Response) {
  try {
    const { country = "US" } = req.body;
    const plan = country === "IN" ? SUBSCRIPTION_PLANS.INR : SUBSCRIPTION_PLANS.USD;

    const orderRequest = {
      body: {
        intent: "CAPTURE",
        purchase_units: [
          {
            amount: {
              currency_code: plan.currency,
              value: plan.price
            },
            description: `Coolors.in Premium Subscription - Monthly (${plan.currency})`
          }
        ],
        application_context: {
          brand_name: "Coolors.in",
          locale: "en-US",
          shipping_preference: "NO_SHIPPING",
          user_action: "PAY_NOW",
          return_url: `${req.protocol}://${req.get('host')}/subscription/success`,
          cancel_url: `${req.protocol}://${req.get('host')}/subscription/cancel`
        }
      },
      prefer: "return=representation"
    };

    const { body, ...httpResponse } = await ordersController.createOrder(orderRequest);
    const jsonResponse = JSON.parse(String(body));
    const httpStatusCode = httpResponse.statusCode;

    res.status(httpStatusCode).json(jsonResponse);
  } catch (error) {
    console.error("Failed to create subscription order:", error);
    res.status(500).json({ error: "Failed to create subscription order." });
  }
}

export async function captureSubscription(req: Request, res: Response) {
  try {
    const { orderId } = req.params;

    const captureRequest = {
      id: orderId,
      prefer: "return=representation"
    };

    const { body, ...httpResponse } = await ordersController.captureOrder(captureRequest);
    const jsonResponse = JSON.parse(String(body));
    const httpStatusCode = httpResponse.statusCode;

    res.status(httpStatusCode).json(jsonResponse);
  } catch (error) {
    console.error("Failed to capture subscription order:", error);
    res.status(500).json({ error: "Failed to capture subscription order." });
  }
}

export async function loadPaypalDefault(req: Request, res: Response) {
  const clientToken = await getClientToken();
  res.json({
    clientToken,
  });
}

