client/src/components/ColorAdjustmentMod.content:
import React, { useState } from 'react';
import { X } from 'lucide-react';
import { Color } from '../types/Color';
import { hexToRgb, rgbToHex, getColorName } from '@/lib/colorUtils';

interface ColorAdjustmentModalProps {
  color: Color;
  onClose: () => void;
  onApply: (color: Color) => void;
}

export default function ColorAdjustmentModal({ color, onClose, onApply }: ColorAdjustmentModalProps) {
  const [red, setRed] = useState(color.rgb.r);
  const [green, setGreen] = useState(color.rgb.g);
  const [blue, setBlue] = useState(color.rgb.b);
  const [currentHex, setCurrentHex] = useState(color.hex);
  const [colorName, setColorName] = useState(color.name || getColorName(color.hex));

  const handleRGBChange = (component: 'r' | 'g' | 'b', value: number) => {
    const newValue = Math.min(255, Math.max(0, value));

    if (component === 'r') setRed(newValue);
    if (component === 'g') setGreen(newValue);
    if (component === 'b') setBlue(newValue);

    // Update hex when RGB changes
    const newHex = rgbToHex(
      component === 'r' ? newValue : red,
      component === 'g' ? newValue : green,
      component === 'b' ? newValue : blue
    );

    setCurrentHex(newHex);
    setColorName(getColorName(newHex));
  };

  const handleHexChange = (hex: string) => {
    // Validate hex format
    if (/^#[0-9A-F]{6}$/i.test(hex)) {
      setCurrentHex(hex);

      // Update RGB values
      const rgb = hexToRgb(hex);
      if (rgb) {
        setRed(rgb.r);
        setGreen(rgb.g);
        setBlue(rgb.b);

        // Update color name
        setColorName(getColorName(hex));
      }
    } else {
      // Just update the input without changing RGB values
      setCurrentHex(hex);
    }
  };

  const handleApply = () => {
    const colorName = getColorName(currentHex);
    onApply({
      hex: currentHex,
      rgb: { r: red, g: green, b: blue },
      locked: color.locked,
      name: colorName
    });
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg p-4 sm:p-6 max-w-md w-full shadow-xl">
        <div className="flex justify-between items-center mb-3 sm:mb-4">
          <h2 className="text-lg sm:text-xl font-bold">Adjust Color</h2>
          <button 
            className="p-1 rounded-full hover:bg-gray-200 transition-colors"
            onClick={onClose}
            aria-label="Close"
          >
            <X size={18} />
          </button>
        </div>

        <div 
          className="w-full h-16 sm:h-24 rounded-lg mb-2"
          style={{ backgroundColor: currentHex }}
        ></div>

        <div className="text-center mb-3 sm:mb-4">
          <span className="text-base sm:text-lg font-medium">{colorName}</span>
        </div>

        <div className="mb-4 sm:mb-6">
          <label className="block text-sm font-medium mb-1">Hex</label>
          <input 
            type="text" 
            className="w-full px-3 py-1.5 sm:py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
            value={currentHex}
            onChange={(e) => handleHexChange(e.target.value)}
          />
        </div>

        <div className="space-y-3 sm:space-y-4 mb-4 sm:mb-6">
          <div>
            <div className="flex justify-between items-center mb-1">
              <label className="text-xs sm:text-sm font-medium">Red</label>
              <span className="text-xs font-mono bg-gray-100 px-1.5 py-0.5 rounded">{red}</span>
            </div>
            <input 
              type="range" 
              min="0" 
              max="255" 
              value={red}
              onChange={(e) => handleRGBChange('r', parseInt(e.target.value))}
              className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer accent-red-500"
            />
          </div>

          <div>
            <div className="flex justify-between items-center mb-1">
              <label className="text-xs sm:text-sm font-medium">Green</label>
              <span className="text-xs font-mono bg-gray-100 px-1.5 py-0.5 rounded">{green}</span>
            </div>
            <input 
              type="range" 
              min="0" 
              max="255" 
              value={green}
              onChange={(e) => handleRGBChange('g', parseInt(e.target.value))}
              className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer accent-green-500"
            />
          </div>

          <div>
            <div className="flex justify-between items-center mb-1">
              <label className="text-xs sm:text-sm font-medium">Blue</label>
              <span className="text-xs font-mono bg-gray-100 px-1.5 py-0.5 rounded">{blue}</span>
            </div>
            <input 
              type="range" 
              min="0" 
              max="255" 
              value={blue}
              onChange={(e) => handleRGBChange('b', parseInt(e.target.value))}
              className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer accent-blue-500"
            />
          </div>
        </div>

        <div className="flex space-x-3">
          <button
            className="flex-1 px-3 sm:px-4 py-1.5 sm:py-2 bg-gray-200 text-gray-800 rounded-md hover:bg-gray-300 transition-colors text-sm sm:text-base"
            onClick={onClose}
          >
            Cancel
          </button>
          <button
            className="flex-1 px-3 sm:px-4 py-1.5 sm:py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition-colors text-sm sm:text-base"
            onClick={handleApply}
          >
            Apply
          </button>
        </div>
      </div>
    </div>
  );
}

client/src/components/ColorCard.tsx.content:
import React from "react";
import { useToast } from "@/hooks/use-toast";
import { Color } from "../types/Color";
import { usePalette } from "@/contexts/PaletteContext";

interface ColorCardProps {
  color: Color;
  index: number;
  onAdjustColor: () => void;
}

export default function ColorCard({ color, index, onAdjustColor }: ColorCardProps) {
  const { toggleLock, removeColor } = usePalette();
  const { toast } = useToast();

  // Determine if we need white or black text based on color brightness
  const textColor = isLightColor(color.hex) ? "text-black" : "text-white";
  const buttonBg = isLightColor(color.hex) 
    ? "bg-white bg-opacity-30 hover:bg-opacity-50" 
    : "bg-black bg-opacity-30 hover:bg-opacity-50";

  const handleCopyColorCode = () => {
    navigator.clipboard.writeText(color.hex).then(() => {
      toast({
        title: "Copied!",
        description: `${color.hex} copied to clipboard`,
        duration: 2000,
      });
    }).catch(err => {
      toast({
        title: "Failed to copy",
        description: "Please try again",
        variant: "destructive",
        duration: 2000,
      });
    });
  };

  const handleToggleLock = (e: React.MouseEvent) => {
    e.stopPropagation();
    toggleLock(index);

    toast({
      title: color.locked ? "Color unlocked" : "Color locked",
      description: color.locked 
        ? "This color will change when generating" 
        : "This color will be preserved when generating",
      duration: 2000,
    });
  };

  const handleRemoveColor = (e: React.MouseEvent) => {
    e.stopPropagation();
    removeColor(index);

    toast({
      title: "Color removed",
      description: "Color has been removed from palette",
      duration: 2000,
    });
  };

  const handleAdjustColor = (e: React.MouseEvent) => {
    e.stopPropagation();
    onAdjustColor();
  };

  // Helper function to determine if a color is light or dark
  function isLightColor(hexColor: string) {
    const r = parseInt(hexColor.slice(1, 3), 16);
    const g = parseInt(hexColor.slice(3, 5), 16);
    const b = parseInt(hexColor.slice(5, 7), 16);

    // Calculate perceived brightness (YIQ formula)
    const yiq = ((r * 299) + (g * 587) + (b * 114)) / 1000;
    return yiq >= 128;
  }

  return (
    <div 
      className="flex-1 relative flex flex-col justify-between transition-all cursor-pointer group min-h-[150px] xs:min-h-[170px] sm:min-h-[190px] md:min-h-0 w-full h-full border-2 border-white/20 rounded-md shadow-md"
      style={{ backgroundColor: color.hex }}
      data-color-index={index}
    >
      {/* Color Controls - Top */}
      <div className="absolute top-0 left-0 right-0 p-2 md:p-4 flex justify-between items-center">
        <div className="flex space-x-1">
          <button 
            className={`${textColor} ${buttonBg} rounded-full p-1.5 md:p-2 transition-all transform hover:scale-110 opacity-90`}
            onClick={handleToggleLock}
            aria-label={color.locked ? "Unlock color" : "Lock color"}
            title={color.locked ? "Unlock color" : "Lock color"}
          >
            <i className={`fas ${color.locked ? 'fa-lock' : 'fa-unlock'} text-xs md:text-base`}></i>
          </button>

          <button 
