  const [showPayment, setShowPayment] = useState(false);
  const { toast } = useToast();

  const plans = {
    free: {
      name: 'Free',
      price: { USD: '$0', INR: 'â‚¹0' },
      period: 'forever',
      features: [
        'Generate up to 7 color palettes',
        'Basic color theory tools',
        'Export as PNG/JSON',
        'Access to FAQ and guides'
      ],
      limitations: [
        'No palette saving',
        'No visualizer access',
        'Limited to 7 palettes',
        'No professional palettes',
        'No cloth color matching'
      ]
    },
    premium: {
      name: 'Premium',
      price: { USD: '$1', INR: 'â‚¹100' },
      period: 'per month',
      features: [
        'Unlimited color palette generation',
        'Save and organize palettes',
        'Full visualizer access',
        'Premium professional palettes',
        'Cloth color palette for your style',
        'Dashboard and product color schemes',
        'Priority support',
        'Export in all formats'
      ],
      limitations: []
    }
  };

  const handleSubscriptionSuccess = async (orderId: string) => {
    if (user) {
      try {
        // Update user subscription status in localStorage
        updateUserSubscription(user.id, {
          subscriptionStatus: 'active',
          subscriptionId: orderId,
          subscriptionPlan: 'premium',
          subscriptionCountry: selectedCountry
        });

        toast({
          title: "Subscription successful!",
          description: "Welcome to Coolors.in Premium! You now have access to all premium features.",
        });

        // Redirect to generator with premium access
        setTimeout(() => {
          window.location.href = '/generate';
        }, 2000);
      } catch (error) {
        console.error('Failed to update subscription:', error);
        toast({
          title: "Subscription processed",
          description: "Your payment was successful. Please refresh the page.",
        });
      }
    } else {
      toast({
        title: "Payment successful",
        description: "Please log in to access your premium features.",
      });
    }
  };

  const handleSubscriptionError = (error: any) => {
    toast({
      title: "Subscription failed",
      description: "There was an issue processing your payment. Please try again.",
      variant: "destructive",
    });
  };

  const handleSubscriptionCancel = () => {
    toast({
      title: "Subscription cancelled",
      description: "You cancelled the subscription process.",
    });
  };

  const startSubscription = () => {
    if (!userInfo.email) {
      toast({
        title: "Email required",
        description: "Please enter your email address to continue.",
        variant: "destructive",
      });
      return;
    }
    setShowPayment(true);
  };

  return (
