/** @type {import('next').NextConfig} */
const nextConfig = {
  reactStrictMode: true,
  swcMinify: true,
  images: {
    domains: [],
    // Optimize images for different devices
    deviceSizes: [640, 750, 828, 1080, 1200, 1920, 2048, 3840],
    // Image formats for better performance
    formats: ['image/webp', 'image/avif'],
    // Minimize image size in production
    minimumCacheTTL: 60,
  },
  compiler: {
    // Remove console.logs in production
    removeConsole: process.env.NODE_ENV === 'production',
  },
  experimental: {
    optimizeCss: true, // Enable CSS optimization
    // Optimize font loading
    fontLoaders: [
      { loader: '@next/font/google', options: { subsets: ['latin'] } },
    ],
  },
  i18n: {
    locales: ['en'],
    defaultLocale: 'en',
  },
  compress: true, // Enable gzip compression
  poweredByHeader: false, // Remove X-Powered-By header for security
  // Configure headers for better security and caching
  async headers() {
    return [
      {
        source: '/(.*)',
        headers: [
          {
            key: 'X-Content-Type-Options',
            value: 'nosniff',
          },
          {
            key: 'X-Frame-Options',
            value: 'DENY',
          },
          {
            key: 'X-XSS-Protection',
            value: '1; mode=block',
          },
          {
            key: 'Referrer-Policy',
            value: 'strict-origin-when-cross-origin',
          },
        ],
      },
      {
        source: '/api/(.*)',
        headers: [
          {
            key: 'Cache-Control',
            value: 'public, max-age=3600, s-maxage=86400',
          },
        ],
      },
      {
        source: '/_next/image(.*)',
        headers: [
          {
            key: 'Cache-Control',
            value: 'public, max-age=31536000, immutable',
          },
        ],
      },
      {
        source: '/icons/(.*)',
        headers: [
          {
            key: 'Cache-Control',
            value: 'public, max-age=31536000, immutable',
          },
        ],
      },
    ];
  },
  // Redirects for better SEO
  async redirects() {
    return [
      {
        source: '/home',
        destination: '/',
        permanent: true,
      },
      {
        source: '/guide',
        destination: '/designers-guide',
        permanent: true,
      },
    ];
  },
};

module.exports = nextConfig;

components/AdSense.tsx.content:
import React, { useEffect } from 'react';
import Script from 'next/script';

export const AdSenseSetup = () => {
  return (
    <Script
      id="adsense-setup"
      strategy="lazyOnload"
      src="https://pagead2.googlesyndication.com/pagead/js/adsbygoogle.js?client=ca-pub-1234567890"
      crossOrigin="anonymous"
    />
  );
};

interface AdSenseProps {
  adSlot: string; // The ad slot ID
  style?: React.CSSProperties; // Optional custom styles
  format?: 'auto' | 'fluid' | 'rectangle' | 'vertical'; // Ad format
  responsive?: boolean; // Whether the ad should be responsive
}

export const AdSense = ({ adSlot, style, format = 'auto', responsive = true }: AdSenseProps) => {
  useEffect(() => {
    try {
      // Only push to adsbygoogle if it exists
      if (typeof window !== 'undefined' && (window as any).adsbygoogle) {
        (window as any).adsbygoogle.push({});
      }
    } catch (err) {
      console.error('AdSense error:', err);
    }
  }, []);

  return (
    <div className="ad-container" style={{ overflow: 'hidden', ...style }}>
      <ins
        className="adsbygoogle"
        style={{
          display: 'block',
          overflow: 'hidden',
          width: responsive ? '100%' : undefined,
          height: format === 'fluid' ? 'fluid' : undefined,
        }}
        data-ad-client="ca-pub-1234567890"
        data-ad-slot={adSlot}
        data-ad-format={format}
        data-full-width-responsive={responsive ? 'true' : 'false'}
      />
    </div>
  );
};

export const InArticleAd = ({ adSlot = '3825791378' }: { adSlot?: string }) => {
  return (
    <div className="my-8 text-center">
      <div className="text-xs text-gray-400 mb-1">Advertisement</div>
      <AdSense 
        adSlot={adSlot} 
        format="fluid" 
        style={{ minHeight: 250 }}
      />
    </div>
