                '@type': 'ListItem',
                'position': 1,
                'name': 'Home',
                'item': 'https://coolors.in/'
              },
              ...items.map((item, index) => ({
                '@type': 'ListItem',
                'position': index + 2,
                'name': item.name,
                'item': `https://coolors.in${item.href}`
              }))
