import React, { useEffect, useState } from 'react';
import type { NextPage } from 'next';
import dynamic from 'next/dynamic';
import { useToast } from '../client/src/hooks/use-toast';
import Header from '../client/src/components/Header';
import KeyboardShortcutsBar from '../client/src/components/KeyboardShortcutsBar';
import ActionButtons from '../client/src/components/ActionButtons';
import ColorCard from '../client/src/components/ColorCard';
import OnboardingTour from '../client/src/components/modals/OnboardingTour';
import ExportModal from '../client/src/components/modals/ExportModal';
import AdjustColorModal from '../client/src/components/modals/AdjustColorModal';
import { usePalette } from '../client/src/contexts/PaletteContext';
import { type Color } from '../client/src/types/Color';
import SEO from '../components/SEO';

// Define any components that must be client-side only
const ClientSideShortcutsBar = dynamic(
  () => import('../client/src/components/KeyboardShortcutsBar'),
  { ssr: false }
);

const HomePage: NextPage = () => {
  const [showOnboarding, setShowOnboarding] = useState(false);
  const [showExportModal, setShowExportModal] = useState(false);
  const [showAdjustModal, setShowAdjustModal] = useState(false);
  const [activeColorIndex, setActiveColorIndex] = useState<number | null>(null);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [draggedIndex, setDraggedIndex] = useState<number | null>(null);
  const [isClient, setIsClient] = useState(false);

  const { palette, generatePalette, addColor, resetPalette, updateColor, reorderColors } = usePalette();
  const { toast } = useToast();

  // Set isClient to true after component mounts
  useEffect(() => {
    setIsClient(true);

    // Check if first visit
    if (typeof window !== 'undefined') {
      const hasVisited = localStorage.getItem("hasVisitedPalettePro");
      if (!hasVisited) {
        setShowOnboarding(true);
        localStorage.setItem("hasVisitedPalettePro", "true");
      }
    }

    // Setup space key handler
    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.code === "Space" && 
          document.activeElement?.tagName !== "INPUT" && 
          document.activeElement?.tagName !== "TEXTAREA" &&
          !showOnboarding && 
          !showExportModal && 
          !showAdjustModal) {
        e.preventDefault();
        handleGeneratePalette();
      }
    };

    document.addEventListener("keydown", handleKeyDown);
    return () => document.removeEventListener("keydown", handleKeyDown);
  }, [showOnboarding, showExportModal, showAdjustModal]);

  const handleGeneratePalette = () => {
    // Call the generate palette function from context
    generatePalette();

    toast({
      title: "New palette generated!",
      description: "Press spacebar for another one.",
      duration: 2000,
    });
  };

  const handleSavePalette = () => {
    // Save to localStorage
    if (typeof window !== 'undefined') {
      const savedPalettes = JSON.parse(localStorage.getItem("savedPalettes") || "[]");
      const newPalette = {
        id: Date.now(),
        colors: palette,
        createdAt: new Date().toISOString(),
      };

      savedPalettes.push(newPalette);
      localStorage.setItem("savedPalettes", JSON.stringify(savedPalettes));

      toast({
        title: "Palette saved!",
        description: "Your palette has been saved to local storage.",
        duration: 2000,
      });
    }
  };

  const handleHelp = () => {
    setShowOnboarding(true);
  };

  const handleExport = () => {
    setShowExportModal(true);
  };

  const handleAdjustColor = (index: number) => {
    setActiveColorIndex(index);
    setShowAdjustModal(true);
  };

  const toggleMobileMenu = () => {
    setMobileMenuOpen(!mobileMenuOpen);
  };

  // Drag and drop handlers for mobile and desktop
  const handleDragStart = (index: number) => {
    setDraggedIndex(index);
  };

  const handleDragOver = (e: React.DragEvent, index: number) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = "move";
  };

  const handleDrop = (e: React.DragEvent, targetIndex: number) => {
    e.preventDefault();
    if (draggedIndex !== null && draggedIndex !== targetIndex) {
      reorderColors(draggedIndex, targetIndex);

      toast({
        title: "Colors reordered",
        description: "Palette order has been updated",
        duration: 2000,
      });
    }
  };

  const handleDragEnd = () => {
    setDraggedIndex(null);
  };

  // Touch events for mobile drag and drop
  const handleTouchStart = (index: number) => {
    setDraggedIndex(index);
  };

  const handleTouchMove = (e: React.TouchEvent) => {
    // Preview of movement handled by CSS
  };

  const handleTouchEnd = (e: React.TouchEvent, targetIndex: number) => {
    if (draggedIndex !== null && draggedIndex !== targetIndex) {
      reorderColors(draggedIndex, targetIndex);

      toast({
        title: "Colors reordered",
        description: "Palette order has been updated",
        duration: 2000,
      });
    }
    setDraggedIndex(null);
  };

  // Server-side rendering fallback content
  if (!isClient) {
    return (
      <div className="flex flex-col h-screen overflow-hidden bg-gray-50">
        <SEO 
          title="Coolors.in - Free Color Palette Generator | Create Beautiful Color Schemes"
          description="Create and explore beautiful color combinations with Coolors.in, the free color palette generator. Design with confidence using our intuitive color tools."
          keywords="color palette generator, color scheme, color combinations, design tools, color inspiration"
          canonical="https://coolors.in/"
          structuredData={{
            '@context': 'https://schema.org',
            '@type': 'WebApplication',
            'name': 'Coolors.in Color Palette Generator',
            'url': 'https://coolors.in/',
            'description': 'Create beautiful color combinations with our intuitive color generator',
            'applicationCategory': 'DesignApplication',
            'operatingSystem': 'Any',
            'offers': {
              '@type': 'Offer',
              'price': '0',
              'priceCurrency': 'USD'
            },
            'featureList': 'Generate color palettes, Save palettes, Export in multiple formats, Visualize palettes in UI templates'
          }}
        />

        <Header 
          onHelp={() => {}} 
          onExport={() => {}} 
          onSave={() => {}}
          mobileMenuOpen={false}
          toggleMobileMenu={() => {}}
        />

        <main className="flex-1 flex flex-col items-center justify-center">
          <div className="text-2xl font-bold mb-4">Loading Color Palette Generator...</div>
          <div className="w-16 h-16 border-t-4 border-blue-500 border-solid rounded-full animate-spin"></div>
        </main>
      </div>
    );
  }

  return (
    <div className="flex flex-col h-screen overflow-hidden bg-gray-50">
      <SEO 
        title="Coolors.in - Free Color Palette Generator | Create Beautiful Color Schemes"
        description="Create and explore beautiful color combinations with Coolors.in, the free color palette generator. Design with confidence using our intuitive color tools."
        keywords="color palette generator, color scheme, color combinations, design tools, color inspiration"
        canonical="https://coolors.in/"
        structuredData={{
          '@context': 'https://schema.org',
          '@type': 'WebApplication',
          'name': 'Coolors.in Color Palette Generator',
          'url': 'https://coolors.in/',
          'description': 'Create beautiful color combinations with our intuitive color generator',
          'applicationCategory': 'DesignApplication',
          'operatingSystem': 'Any',
          'offers': {
            '@type': 'Offer',
            'price': '0',
            'priceCurrency': 'USD'
          },
          'featureList': 'Generate color palettes, Save palettes, Export in multiple formats, Visualize palettes in UI templates'
        }}
      />

      <Header 
        onHelp={handleHelp} 
        onExport={handleExport} 
        onSave={handleSavePalette}
        mobileMenuOpen={mobileMenuOpen}
        toggleMobileMenu={toggleMobileMenu}
      />

      <ClientSideShortcutsBar />

      <main>
        {/* Mobile View: Stack colors vertically with drag support */}
        <section className="flex-1 flex flex-col overflow-auto md:hidden" id="mobilePaletteContainer" aria-label="Color Palette - Mobile View">
          {palette.map((color, index) => (
            <article 
              key={index}
              className={`relative mb-10 ${draggedIndex === index ? 'opacity-60 border-2 border-dashed border-gray-400' : ''}`}
              draggable={true}
              onDragStart={() => handleDragStart(index)}
              onDragOver={(e) => handleDragOver(e, index)}
              onDrop={(e) => handleDrop(e, index)}
              onDragEnd={handleDragEnd}
              onTouchStart={() => handleTouchStart(index)}
              onTouchMove={handleTouchMove}
              onTouchEnd={(e) => handleTouchEnd(e, index)}
            >
              {/* Clear separation between palettes */}
              <div className="w-full flex flex-col">
                <header className="bg-gray-100 dark:bg-gray-800 py-1 px-2 rounded-t-md text-xs opacity-80 text-center shadow-sm">
                  <h2>Color {index+1}</h2> 
                  <span className="ml-2 inline-flex items-center text-gray-500">
                    <i className="fas fa-grip-lines text-xs mr-1" aria-hidden="true"></i>
                    <span className="sr-only">Draggable</span>
                    drag to reorder
                  </span>
                </header>
                <div className="absolute top-10 left-2 z-10 p-3 rounded-full bg-black bg-opacity-50 touch-manipulation cursor-grab active:cursor-grabbing">
                  <i className="fas fa-grip-lines text-sm text-white" aria-hidden="true"></i>
                  <span className="sr-only">Drag handle</span>
                </div>
                <ColorCard 
                  color={color}
                  index={index}
                  onAdjustColor={() => handleAdjustColor(index)}
                />
              </div>
            </article>
          ))}
        </section>

        {/* Desktop View: Colors side by side with drag support */}
        <section className="hidden md:flex flex-1 flex-row overflow-hidden" id="desktopPaletteContainer" aria-label="Color Palette - Desktop View">
          {palette.map((color, index) => (
            <article 
              key={index}
              className={`relative flex-1 ${draggedIndex === index ? 'opacity-60 border-2 border-dashed border-gray-400' : ''}`}
              draggable={true}
              onDragStart={() => handleDragStart(index)}
              onDragOver={(e) => handleDragOver(e, index)}
              onDrop={(e) => handleDrop(e, index)}
              onDragEnd={handleDragEnd}
              onTouchStart={() => handleTouchStart(index)}
              onTouchMove={handleTouchMove}
              onTouchEnd={(e) => handleTouchEnd(e, index)}
            >
              <div className="absolute top-3 left-3 z-20 p-3 rounded-full bg-black bg-opacity-40 cursor-grab active:cursor-grabbing transition-opacity opacity-70 hover:opacity-100">
                <i className="fas fa-grip-lines text-white" aria-hidden="true"></i>
                <span className="sr-only">Drag handle for color {index+1}</span>
              </div>
              <ColorCard 
                color={color}
                index={index}
                onAdjustColor={() => handleAdjustColor(index)}
              />
            </article>
          ))}

          <div className="flex items-center justify-center w-16 bg-gray-100 border-l border-gray-300 hover:bg-gray-200 cursor-pointer transition-colors"
              onClick={() => addColor()} aria-label="Add new color">
            <div className="flex flex-col items-center justify-center text-gray-500 space-y-2">
              <i className="fas fa-plus text-xl" aria-hidden="true"></i>
              <span className="text-xs font-medium">Add</span>
            </div>
          </div>
        </section>
      </main>

      <ActionButtons 
        onGenerate={handleGeneratePalette}
        onAddColor={addColor}
        onClearAll={resetPalette}
      />

      {showOnboarding && <OnboardingTour onClose={() => setShowOnboarding(false)} />}

      {showExportModal && 
        <ExportModal 
          palette={palette} 
          onClose={() => setShowExportModal(false)} 
        />
      }

      {showAdjustModal && activeColorIndex !== null &&
        <AdjustColorModal 
          color={palette[activeColorIndex]} 
          onClose={() => setShowAdjustModal(false)}
          onApply={(updatedColor: Color) => {
            updateColor(activeColorIndex, updatedColor);
            setShowAdjustModal(false);
          }}
        />
      }
    </div>
  );
};

export default HomePage;

pages/visualizer-new.tsx.content:
import React, { useState, useEffect } from 'react';
import type { NextPage } from 'next';
import Link from 'next/link';
import Head from 'next/head';
import { Layout, Monitor, PieChart, MessageSquare, Calendar, ArrowLeft, ArrowRight, Check } from 'lucide-react';
import { isLightColor } from '../client/src/lib/colorUtils';
import { Color } from '../shared/schema';
import CommonHead from '../components/CommonHead';
import Footer from '../client/src/components/Footer';

// Template types
type TemplateType = 'dashboard' | 'landing' | 'analytics' | 'chat' | 'calendar';

// Server-side props to pass initial palette data
export async function getServerSideProps() {
  // Default palette for initial render
  const defaultPalette: Color[] = [
    { hex: '#1e293b', rgb: { r: 30, g: 41, b: 59 }, locked: false },
    { hex: '#334155', rgb: { r: 51, g: 65, b: 85 }, locked: false },
    { hex: '#3b82f6', rgb: { r: 59, g: 130, b: 246 }, locked: false },
    { hex: '#ffffff', rgb: { r: 255, g: 255, b: 255 }, locked: false },
    { hex: '#f8fafc', rgb: { r: 248, g: 250, b: 252 }, locked: false }
  ];

  return {
    props: {
      initialPalette: defaultPalette
    }
  };
}

const VisualizerPage: NextPage<{ initialPalette: Color[] }> = ({ initialPalette }) => {
  const [palette, setPalette] = useState<Color[]>(initialPalette);
  const [activeTemplate, setActiveTemplate] = useState<TemplateType>('dashboard');
  const [showExportToast, setShowExportToast] = useState(false);
  const [colorIndicators, setColorIndicators] = useState(false);

  // Check if we're in the browser to access localStorage
  useEffect(() => {
    try {
      const savedPaletteJson = localStorage.getItem('currentPalette');
      if (savedPaletteJson) {
        const savedPalette = JSON.parse(savedPaletteJson);
        if (Array.isArray(savedPalette) && savedPalette.length > 0) {
          console.log('Next.js: Loaded palette from localStorage:', savedPalette);
          setPalette(savedPalette);
        }
      }
    } catch (err) {
      console.error('Error loading palette from localStorage:', err);
    }
  }, [setPalette]);

  // Listen for palette updates from the generator page
  useEffect(() => {
    const handleStorageChange = (e: StorageEvent) => {
      if (e.key === 'currentPalette') {
        try {
          const updatedPalette = JSON.parse(e.newValue || '[]');
          if (Array.isArray(updatedPalette) && updatedPalette.length > 0) {
            setPalette(updatedPalette);
          }
        } catch (err) {
          console.error('Error parsing palette from storage event:', err);
        }
      }
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  // Helper to get text color based on background for contrast
  const getTextColor = (bgColor: string): string => {
    return isLightColor(bgColor) ? '#1f2937' : '#ffffff';
  };

  // Export current template as image
  const exportAsImage = async () => {
    if (typeof window !== 'undefined') {
      try {
        // Dynamically import html2canvas (client-side only)
        const html2canvas = (await import('html2canvas')).default;
        const visualizerEl = document.getElementById('template-visualizer');

        if (!visualizerEl) return;

        const canvas = await html2canvas(visualizerEl, {
          backgroundColor: null,
          scale: 2 // Higher resolution
        });

        const image = canvas.toDataURL('image/png');
        const link = document.createElement('a');
        link.href = image;
        link.download = `${activeTemplate}-template-${new Date().getTime()}.png`;
        link.click();

        // Show success toast
        setShowExportToast(true);
        setTimeout(() => setShowExportToast(false), 3000);
      } catch (err) {
        console.error('Error exporting template:', err);
      }
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4 md:p-8 flex flex-col">
      <CommonHead 
        title="Palette Visualizer | See Your Colors in Real UI Templates - Coolors.in"
        description="Visualize your color palette in real UI templates. See how your colors work together in dashboards, landing pages, and app interfaces."
        keywords="palette visualizer, color combinations, UI templates, color schemes in action"
        canonicalUrl="https://coolors.in/visualizer"
        structuredData={{
          "@context": "https://schema.org",
          "@type": "WebPage",
          "name": "Palette Visualizer | Coolors.in",
          "url": "https://coolors.in/visualizer",
          "description": "Visualize your color palette in real UI templates with Coolors.in's Palette Visualizer",
          "isPartOf": {
            "@type": "WebApplication",
            "name": "Coolors.in"
          }
        }}
      />

      <header className="mb-6">
        <div className="flex items-center justify-between">
          <h1 className="text-3xl font-bold text-gray-800 bg-gradient-to-r from-purple-600 to-blue-400 bg-clip-text text-transparent">
            Palette Visualizer
          </h1>
          <Link href="/" className="flex items-center text-gray-600 hover:text-gray-900 transition-colors">
            <ArrowLeft className="mr-1" size={20} />
            Back to Generator
          </Link>
        </div>
        <p className="text-gray-600 mt-2">
          See how your color palette would look in different UI templates
        </p>
      </header>

      {/* Top action bar */}
      <div className="bg-white rounded-lg shadow-md p-4 mb-4">
        <div className="flex flex-wrap justify-between items-center">
          <div className="flex flex-wrap gap-2">
            <TemplateButton 
              active={activeTemplate === 'dashboard'} 
              onClick={() => setActiveTemplate('dashboard')}
              icon={<Layout size={18} />}
              label="Dashboard"
            />
            <TemplateButton 
              active={activeTemplate === 'landing'} 
              onClick={() => setActiveTemplate('landing')}
              icon={<Monitor size={18} />}
              label="Landing Page"
            />
            <TemplateButton 
              active={activeTemplate === 'analytics'} 
              onClick={() => setActiveTemplate('analytics')}
              icon={<PieChart size={18} />}
              label="Analytics"
            />
            <TemplateButton 
              active={activeTemplate === 'chat'} 
              onClick={() => setActiveTemplate('chat')}
              icon={<MessageSquare size={18} />}
              label="Chat App"
            />
            <TemplateButton 
              active={activeTemplate === 'calendar'} 
              onClick={() => setActiveTemplate('calendar')}
              icon={<Calendar size={18} />}
              label="Calendar"
            />
          </div>
          <div className="flex items-center gap-3 mt-3 sm:mt-0">
            <div className="flex items-center">
              <input 
                type="checkbox" 
                id="color-indicators" 
                className="mr-2"
                checked={colorIndicators}
                onChange={() => setColorIndicators(!colorIndicators)} 
              />
              <label htmlFor="color-indicators" className="text-sm text-gray-600">Show color indicators</label>
            </div>
            <button 
              onClick={exportAsImage} 
              className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors flex items-center gap-1"
            >
              <ArrowRight size={16} />
              Export as PNG
            </button>
          </div>
        </div>
      </div>

      {/* Template visualization */}
      <div 
        id="template-visualizer" 
        className="bg-white rounded-xl shadow-lg overflow-hidden flex-1 mb-6 relative"
      >
        {activeTemplate === 'dashboard' && (
          <DashboardTemplate palette={palette} getTextColor={getTextColor} showIndicators={colorIndicators} />
        )}
        {activeTemplate === 'landing' && (
          <LandingTemplate palette={palette} getTextColor={getTextColor} showIndicators={colorIndicators} />
        )}
        {activeTemplate === 'analytics' && (
          <AnalyticsTemplate palette={palette} getTextColor={getTextColor} showIndicators={colorIndicators} />
        )}
        {activeTemplate === 'chat' && (
          <ChatTemplate palette={palette} getTextColor={getTextColor} showIndicators={colorIndicators} />
        )}
        {activeTemplate === 'calendar' && (
          <CalendarTemplate palette={palette} getTextColor={getTextColor} showIndicators={colorIndicators} />
        )}
      </div>

      {/* Color palette reference */}
      <div className="bg-white rounded-lg shadow p-4">
        <h3 className="text-lg font-medium mb-2">Your Palette</h3>
        <div className="flex h-12 rounded-md overflow-hidden">
          {palette.map((color, index) => (
            <div 
              key={index} 
              className="flex-1" 
              style={{ backgroundColor: color.hex }}
            >
              <div className="h-full flex items-center justify-center">
                <span className={`text-xs font-medium ${isLightColor(color.hex) ? 'text-gray-800' : 'text-white'}`}>
                  {color.hex}
                </span>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Export success toast */}
      {showExportToast && (
        <div className="fixed bottom-4 right-4 bg-green-100 border-l-4 border-green-500 text-green-700 p-4 rounded shadow-md flex items-center">
          <Check size={20} className="mr-2" />
          <span>Template exported successfully!</span>
        </div>
      )}

      {/* Footer */}
      <Footer />
    </div>
  );
};

// Template button component
interface TemplateButtonProps {
  active: boolean;
  onClick: () => void;
  icon: React.ReactNode;
  label: string;
}

function TemplateButton({ active, onClick, icon, label }: TemplateButtonProps) {
  return (
    <button
      onClick={onClick}
      className={`px-4 py-2 rounded-md font-medium flex items-center gap-2 transition-colors ${
        active 
          ? "bg-gradient-to-r from-purple-600 to-blue-500 text-white" 
          : "bg-gray-100 text-gray-700 hover:bg-gray-200"
      }`}
    >
      {icon}
      {label}
    </button>
  );
}

// Color indicator component
interface ColorIndicatorProps {
  color: string;
  index: number;
  showIndicators: boolean;
}

function ColorIndicator({ color, index, showIndicators }: ColorIndicatorProps) {
  if (!showIndicators) return null;

  return (
    <div 
